{% set rdata = [] %}
{% set er = ['category', 'confidence', 'sandbox_name', 'malware_names', 'malware_classification'] %}
{% if vars.steps.Get_File_Reputation.data.data[0].attributes.sandbox_verdicts %}
{% for key, value in vars.steps.Get_File_Reputation.data.data[0].attributes.sandbox_verdicts.items() %}
{% set _ = rdata.append(value) %}
{% endfor %}
{% for item in rdata %}
{% set _np = er | difference(item.keys()) %}
{% set _temp = item %}
{% if _np | length > 0%}
{% for i in _np %}
{% set _add = item.update({i:'NA'}) %}
{% endfor %}
{% endif %}
{% for k, v in item.items() %}
{% if v is iterable and (v is not string and v is not mapping and v is not integer) %}
{% set _ = item.update({k: v | join(',') }) %}
{% elif v is integer %}
{% set _ =  item.update({k: v | string }) %}
{% endif %}
{% endfor %}
{% set _res = vars.sandbox_verdict.append(dict(item | dictsort)) %}
{% endfor %}
{% endif %}

